  public static function borrarPreregistro($id){
    if (empty($id)){ return false; } 
    return $GLOBALS['db']->delete('mfo_preregistro','id = '.$id);
  }

  public static function consultarPre_registrados($correos){
    if (empty($correos)){ return false; } 

    $sql = "SELECT *
            FROM mfo_preregistro where tipo_usuario = 1 and correo IN(".$correos.");";
    return $GLOBALS['db']->auto_array($sql,array(),true);   
